import { useState } from "react";
import "./form.scss";
import { Button, TextField } from "@mui/material";
import { checkName } from "../../validations/formValidation";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

const Form = () => {
  const [data, setData] = useState({
    firstName: "ff",
    lastName: "ff",
    email: "fff@fff.com",
    birthDate: null,
    city: "feff",
    zip: "06666",
  });

  const canSubmit =
    data.firstName &&
    data.lastName &&
    data.email &&
    data.birthDate &&
    data.city &&
    data.zip;

  const checkInputValidity = () => {

  }

  const submit = (event) => {
    const nameValidity = checkName(data.firstName);
    console.log(nameValidity)
    toast("Wow so easy!");
    event.preventDefault();
  };


  return (
    <>
      <form className="form">
        <TextField
          id="outlined-basic"
          label="Nom"
          value={data.lastName}
          variant="outlined"
          onChange={(event) =>
            setData({ ...data, lastName: event.target.value })
          }
        />
        <TextField
          id="outlined-basic"
          label="PrÃ©nom"
          value={data.firstName}
          variant="outlined"
          onChange={(event) =>
            setData({ ...data, firstName: event.target.value })
          }
        />
        <TextField
          id="outlined-basic"
          label="Email"
          value={data.email}
          type="email"
          variant="outlined"
          onChange={(event) => setData({ ...data, email: event.target.value })}
        />
        <TextField
          id="outlined-basic"
          label="Date de naissance"
          value={data.birthDate}
          type="date"
          variant="filled"
          onChange={(event) =>
            setData({ ...data, birthDate: event.target.value })
          }
        />
        <TextField
          id="outlined-basic"
          label="Ville"
          value={data.city}
          variant="outlined"
          onChange={(event) => setData({ ...data, city: event.target.value })}
        />
        <TextField
          id="outlined-basic"
           
          label="Code postal"
                    value={data.lastName}

          variant="outlined"
          onChange={(event) => setData({ ...data, zip: event.target.value })}
        />

        <Button
          type="submit"
          variant="contained"
          color="primary"
          disabled={!canSubmit}
          onClick={(event) => submit(event)}
        >
          Soumettre
        </Button>
      </form>
      <ToastContainer />
    </>
  );
};

export default Form;
