const checkName = (name, label) => {
    const isValid = true; 
    const message = "";
    const regex = /^[a-zA-Z']+$/;

    console.log(regex.test(name))
    
    switch (name) {
        case !name:
            message = `Veuillez renseigner un ${label}`
            isValid = false
            break;
        
        case !regex.test(name):
            message = `Le ${label} ne doit pas contenir de chiffre`
            isValid = false
            break;
        
        default:
            break;
    }
    return {isValid: isValid , message: ""} 
}

export {checkName}
