let isValid = true; 
let message = "";

const checkName = (name, label) => {
    const regex = /^[a-zA-Z']+$/;
    
    if (!name) {
        message = `Veuillez renseigner un ${label}`;
        isValid = false;
    } else if (!regex.test(name)) {
        message = `Le ${label} ne doit pas contenir de chiffre ou de caractère spécial`;
        isValid = false;
    }
    return {isValid: isValid , message: {target: label === "nom" ? "lastName" : "firstName", value: message}  } 
}

const checkEmail = (email) => {
    const regex = /^[@]+$/

    if(!email) {
        message = `Veuillez renseigner un email`
        isValid = false
    }else if (!regex.test(email)){
        message
    }
}

export {checkName}
