import { useState } from "react";
import "./form.scss";
import { Button, TextField } from "@mui/material";
import { checkName } from "../../validations/formValidation";

const Form = () => {
  const [data, setData] = useState({
    firstName: "",
    lastName: "",
    email: "",
    birthDate: null,
    city: "",
    zip: "",
  });

  const canSubmit = data.firstName &&
                    data.lastName &&
                    data.email &&
                    data.birthDate &&
                    data.city &&
                    data.zip
                    
  const submit = (event) => {
    checkName(data.firstName)
    // event.preventDefault()
  }

  return (
    <form className="form">
      <TextField
        id="outlined-basic"
        label="Nom"
        variant="outlined"
        onChange={(event) => setData({ ...data, lastName: event.target.value })}
      />
      <TextField
        id="outlined-basic"
        label="PrÃ©nom"
        variant="outlined"
        onChange={(event) =>
          setData({ ...data, firstName: event.target.value })
        }
      />
      <TextField
        id="outlined-basic"
        label="Email"
        type="email"
        variant="outlined"
        onChange={(event) => setData({ ...data, email: event.target.value })}
      />
      <TextField
        id="outlined-basic"
        label="Date de naissance"
        type="date"
        variant="filled"
        onChange={(event) =>
          setData({ ...data, birthDate: event.target.value })
        }
      />
      <TextField
        id="outlined-basic"
        label="Ville"
        variant="outlined"
        onChange={(event) => setData({ ...data, city: event.target.value })}
      />
      <TextField
        id="outlined-basic"
        label="Code postal"
        variant="outlined"
        onChange={(event) => setData({ ...data, zip: event.target.value })}
      />

      <Button type="submit" variant="contained" color="primary" disabled={!canSubmit} onClick={(event) => submit(event)}>
        Soumettre
      </Button>
    </form>
  );
};

export default Form;
